<?php

namespace SlidesLive\SlidesLiveBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * CategoryRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CategoryRepository extends EntityRepository {
	
	public function listAllCategories() {
		$em = $this->getEntityManager();
		$query = $em->createQuery(
			'SELECT b
			FROM SlidesLiveBundle:Category b'
		);
		$recievedCategories = $query->getResult();
		return $recievedCategories;
	}
	
	public function findCategoryIdAccordingName($categoryName) {
		$em = $this->getEntityManager();
		$query = $em->createQuery(
		  'SELECT c
		  FROM SlidesLiveBundle:Category c
		  WHERE c.name = :categoryName
		  ')
		  ->setParameters(array(
			  'categoryName' => $categoryName
			)
		  );
		try {
		  return $query->getSingleResult();
		}  
		catch (\Exception $e) {
			echo "je konec";
		  return null;
		}  		
	}
	
	public function findPresentations($categoryId) {
		echo $categoryId;
		
        $em = $this->getEntityManager();
        $query = $em->createQuery("
            SELECT p FROM SlidesLiveBundle:Presentation p
            JOIN p.categories c
            WHERE c.id = :categoryId
            ORDER BY p.dateRecorded DESC")
        ->setParameters(array(
          'categoryId' => sprintf('%s', $categoryId))
        );

		$recievedPresentations = $query->getResult();
		return $recievedPresentations;
	}			

}